import React, { Component } from 'react';
import './Signin.css'
import Navbar from '../Navbar/Navbar.js'
import * as firebase from 'firebase'
import * as admin from 'firebase-admin';
import serviceAccount from '../../firebase/serviceKey.json';


admin.initializeApp({
  credential: admin.credential.cert(serviceAccount),
  databaseURL: 'https://actandcare-5cf3c.firebaseio.com/'
}); 


export default class Admin extends Component {
  constructor(props) {
    super(props);
    this.state = { listAccounts: [], loading: true }
    this.display = this.display.bind(this)
  }

  componentDidMount() {
    this.fetch()
  }
  
  
  listAllUsers(nextPageToken) {
    admin.auth().listUsers(25, nextPageToken)
      .then(function(listUsersResult) {
        listUsersResult.users.forEach(function(userRecord) {
          console.log("user", userRecord.toJSON());
        });
        if (listUsersResult.pageToken) {
          // List next batch of users.
          this.listAllUsers(listUsersResult.pageToken)
        }
      })
      .catch(function(error) {
        console.log("Error listing users:", error);
      });
  }

  fetch() {

    const ref = firebase.database().ref('/users');
    ref.on('value', snap => {
      snap.forEach(child => {
        this.setState({
          listAccounts: this.state.listAccounts.concat(child.val()),
          loading: false
        })
      })
    })
  }

  display() {
    if (this.state.loading === true) return <div>Updating...</div>
    else if (this.state.listAccounts !== "") return this.state.listAccounts.map((account, index) => {
      return (
        <tr id={index}>
          <th scope="col">RPPS: {account.RPPS}</th>
          <th scope="col">Date de naissance: {account.bdate}</th> 
          <th scope="col">Rue: {account.rue}</th>
          <th scope="col">Specialit√©: {account.specialite}</th>
          <th scope="col">Statut: {account.statut}</th>
          <th scope="col">Telephone: {account.tel}</th>
          <th scope="col">Region: {account.region}</th>
          <th scope="col">Ville: {account.ville}</th>
          <button>Valider </button>
          <button>Rendre admin</button>
          <br></br>
          <br></br>
          <br></br>
        </tr>
        )
    })
  }


  render() {
    
    return (
      <div>
          <header>
            <Navbar></Navbar>
          </header>
        <div id="wrapper">
          <form id="login">
            <h2>Accounts Management</h2>

            <div id="container" className="col-md-9">
              <br></br>
              <br></br>
              <div>
                {this.display()}
              </div>
              <br></br>
            </div>
          </form>
        </div>
      </div>

    );
  }
}
