import React, { Component } from 'react';
import './Signin.css'

export default class Password extends Component {
  constructor(props) {
    super(props);
    state={password:""}
  }
  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value })
  }

  updatePassword(){
    this.props.user.updatePassword(this.state.password).then(function() {
      alert("updated")
    }).catch(function(error) {
      // An error happened.
    });
  }

  render() {

    const {user} = this.props;
    return (
          <div class="container" >
            <h1 > Mon compte </h1>
            <form onSubmit={this.updatePassword.bind(this)} >

              <br />

              <div class="form-row">
                <div class="col-md-6">

                  <div class="form-group">
                    <div class="form-row">
                      <div class="col-md-6">
                        <label for="typedetablissement"><b>Mot de passe: </b></label>
                      </div>
                      <div class="col-md-6">
                        <input type="text" name="password" value={this.state.password} class="form-control" onChange={this.handleChange.bind(this)} />
                      </div>
                    </div>
                  </div>

              

                </div></div>

              <div class="form-row">
                <button type="submit" class="btn btn-md btn-block" id="addNewElement" >Modifier</button>
              </div>

            </form>


          </div>
      
    );
  }
}
