import React, {Component} from 'react';
import './AjoutMissions.css'
import * as firebase from 'firebase';


export default class AjoutMissions extends Component {
  constructor(props) {
        super(props);
        this.state = {
          nomission:1
        };

        this.getMissions=this.getMissions.bind(this);
        this.componentDidMount=this.componentDidMount.bind(this)
      }

  componentDidMount(){
    const ref = firebase.database().ref('missions');
    //this.setState({nomission:3})
    ref.on('value', snap=>{
      snap.forEach(child=>{
        this.setState({
          nomission:child.nomission
      })
      })})

  }

  getMissions(){
    let i=0;
    let nomission1="";
    let statut1= "";

    const ref = firebase.database().ref('missions');
    ref.once('value').then( function(snapshot){
      let mission=snapshot.val();
      nomission1=mission.nomission;
      statut1=mission.statut;
      this.setState({
        listMissions:this.state.listMissions.concat({mission:nomission1, statut:statut1})
      });
      })
      return(
        <div>
          <br></br>
          <br></br>
          <br></br>

        </div>
      )
  }








  render() {
    return(
      <div id="wrapper">
      <div class="container">


        <form>
        <br></br>


      <div class="form-group">
      <div class="form-row"> <div class="col-md-6">
          <h3>Num√©ro de mission:</h3><p>{this.state.nomission}</p>
      </div></div>
      </div>


      </form>


      </div>
      </div>

    );

  }
}
