import React, { Component } from 'react';
import './Signin.css'
import Navbar from '../Navbar/Navbar.js'
import * as firebase from 'firebase'

export default class Account extends Component {
  constructor(props) {
    super(props);
    this.state = { listAccounts: [], listKeys: [], loading: true }
    this.display = this.display.bind(this)
  }

  componentDidMount() {
    var user = firebase.auth().currentUser;
    if (user) {
      this.setState({ user, loading: false })
    } else {
      alert("User not signed in !!!")
    }
    const ref = firebase.database().ref('/users/' + user.uid);
    ref.on('value', (snap) => {
      let account = snap.val()
      this.setState({ account })
    })
  }
  display() {
    //let account = this.state.user_info
    //const { account} = this.state;
    return <table class="table table-striped table-responsive">
      <tr>
        <th scope="col">Email: {this.state.account.email}</th>
        <th scope="col">Nom: {this.state.account.name}</th>
        <th scope="col">RPPS: {this.state.account.RPPS}</th>
        <th scope="col">Date de naissance: {this.state.account.bdate}</th>
        <th scope="col">Rue: {this.state.account.rue}</th>
        <th scope="col">Specialité: {this.state.account.specialite}</th>
        <th scope="col">Téléphone: {this.state.account.tel}</th>
        <th scope="col">Région: {this.state.account.region}</th>
        <th scope="col">Ville: {this.state.account.ville}</th>
        <th scope="col" className="alert alert-danger">Statut: {this.state.account.statut}</th>
      </tr>
    </table>

  }

  /*  fetch() {
     const ref = firebase.database().ref('/users');
     ref.on('value', snap => {
       //https://css-tricks.com/intro-firebase-react/
       let newAccounts = [];
       let newKeys = []
       snap.forEach(
         child => {
           newAccounts.push(child.val())
           newKeys.push(child.key)
         })
       this.setState({
         //listKeys: this.state.listKeys.concat(child.key),
         //listAccounts: this.state.listAccounts.concat(child.val()),
         listAccounts: newAccounts,
         listKeys: newKeys,
         loading: false
       })
     })
   } */

  render() {

    return (
      <div>
        <header>
          <Navbar></Navbar>
        </header>
        <div id="wrapper">
          <form id="login">
            <h2>Mon compte</h2>

            <div id="container" className="col-md-9">
              <br></br>
              <br></br>
              <div>
                {this.display()}
              </div>
              <br></br>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
