import React from 'react';
import {Line} from 'react-chartjs-2';

//const labelstab = ['Lundi 22 Octobre', 'Mardi 23 Octobre', 'Mercredi 24 Octobre', 'Jeudi 25 Octobre', 'Vendredi 26 Octobre', 'Samedi 27 Octobre', 'Dimanche 28 Octobre'];
//const datatab = [65, 59, 80, 81, 56, 55, 40];
export default class line extends React.Component{
  constructor(props) {
        super(props);
        this.state = {
          visites: props.visites,
          dates: props.dates};

}
  render() {

    return (
      <div>
        <h2>Nombre de visiteurs</h2>
        <Line data={data} />
      </div>
    );
  }
};


const data = {
  labels: this.state.dates,
  datasets: [
    {
      label: 'Nombre de visites',
      fill: false,
      lineTension: 0.1,
      backgroundColor: 'rgba(75,192,192,0.4)',
      borderColor: 'rgba(75,192,192,1)',
      borderCapStyle: 'butt',
      borderDash: [],
      borderDashOffset: 0.0,
      borderJoinStyle: 'miter',
      pointBorderColor: 'rgba(75,192,192,1)',
      pointBackgroundColor: '#fff',
      pointBorderWidth: 1,
      pointHoverRadius: 5,
      pointHoverBackgroundColor: 'rgba(75,192,192,1)',
      pointHoverBorderColor: 'rgba(220,220,220,1)',
      pointHoverBorderWidth: 2,
      pointRadius: 1,
      pointHitRadius: 10,
      data: this.state.visites
    }
  ]
};
