import React, { Component } from 'react';
import './Signin.css'
import Navbar from '../Navbar/Navbar.js'
import * as firebase from 'firebase'

export default class Account extends Component {
  constructor(props) {
    super(props);
    this.state = { listAccounts: [], listKeys: [], loading: true }
  }

  componentDidMount() {
    var user = firebase.auth().currentUser;
    if (user) {
      this.setState({user, loading:false})
    } else {
      alert("User not signed in !!!")
    }
    const ref = firebase.database().ref('/users/'+user.uid);
    ref.on('value', snap=>{
      user_info=snap.val()
      this.setState({user_info})
    })
  }

 /*  fetch() {
    const ref = firebase.database().ref('/users');
    ref.on('value', snap => {
      //https://css-tricks.com/intro-firebase-react/
      let newAccounts = [];
      let newKeys = []
      snap.forEach(
        child => {
          newAccounts.push(child.val())
          newKeys.push(child.key)
        })
      this.setState({
        //listKeys: this.state.listKeys.concat(child.key),
        //listAccounts: this.state.listAccounts.concat(child.val()),
        listAccounts: newAccounts,
        listKeys: newKeys,
        loading: false
      })
    })
  } */

  render() {

    return (
      <div>
        <header>
          <Navbar></Navbar>
        </header>
        <div id="wrapper">
          <form id="login">
            <h2>Mon compte</h2>

            <div id="container" className="col-md-9">
              <br></br>
              <br></br>
              <div>
                {this.display()}
              </div>
              <br></br>
            </div>
          </form>
        </div>
      </div>

    );
  }
}
