import React, { Component } from 'react';
import './Signin.css'
import Navbar from '../Navbar/Navbar.js'
import * as firebase from 'firebase'

export default class Account extends Component {
  constructor(props) {
    super(props);
    this.state = {loading: true, modif:false}
    //this.display = this.display.bind(this)
  }

  componentDidMount() {
    var user = firebase.auth().currentUser;
    if (user) {
      this.setState({ user})
    } else {
      alert("User not signed in !!!")
    }
    const ref = firebase.database().ref('/users/' + user.uid);
    ref.on('value', (snap) => {
      let account = snap.val()
      //alert(account.email, account.RPPS, account.region)
      this.setState({ account, loading: false  })
    })
  }
  update(name, value,e){
    e.preventDefault();
    firebase.database().ref('users/' + this.state.user.uid).update(
      {
        [name]:[value]
      })
    }

  render() {
    const { account, loading, modif } = this.state;
    if(loading)return <p>Updating ...</p>
    else if(!modif) return (
      <div>
        <header>
          <Navbar></Navbar>
        </header>
        <div id="wrapper">
          <form id="login">
            <h2>Mon compte</h2>

            <div id="container" className="col-md-9">
              <br></br>
              <br></br>
              <div>

                <table class="table table-striped">
                  <tr>
                    <th scope="col">Email: {account.email} <button name="email" onClick={this.update.bind("email")}>Modifier</button></th>
                    <th scope="col">Nom: {account.name}<button name="name" onClick={this.update.bind("name")}>Modifier</button></th>
                    <th scope="col">RPPS: {account.RPPS}<button name="RPPS" onClick={this.update.bind("RPPS")}>Modifier</button></th>
                    <th scope="col">Date de naissance: {account.bdate}<button name="bdate" onClick={this.update.bind("bdate")}>Modifier</button></th>
                    <th scope="col">Rue: {account.rue}<button name="rue" onClick={this.update.bind("rue")}>Modifier</button></th>
                    <th scope="col">Specialité: {account.specialite}<button name="specialite" onClick={this.update.bind("specialite")}>Modifier</button></th>
                    <th scope="col">Téléphone: {account.tel}<button name="tel" onClick={this.update.bind("tel")}>Modifier</button></th>
                    <th scope="col">Région: {account.region}<button name="account" onClick={this.update.bind("region")}>Modifier</button></th>
                    <th scope="col">Ville: {account.ville}<button name="ville" onClick={this.update.bind("ville")}>Modifier</button></th>
                    <th scope="col" className="alert alert-danger">Statut: {account.statut}</th>
                  </tr>
                  <button onClick={this.setState({modif:true})}>Modifier</button>
                </table>


              </div>
              <br></br>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
