import React from 'react';
import './AjoutMissions.css'

export default class AjoutMissions extends React.Component {
  constructor(props) {
        super(props);
        this.state = {
          region: "",
          ch: "",
          specialite: "",
          type: "",
          datededebut: "",
          datedefin: "",
          heurededebut: "",
          heuredefin: "",
          remuneration: "",
          commentaires: ""          
        }
      }

  render() {

    return (
    <div class="container" >
      <h1> Ajouter une mission</h1>
        <form >

        <div class="form-group">
          <label for="nomdelattraction">Nom de l'attraction</label>
          <input type="text" class="form-control" value="{this.state.name}" onChange={this.handleNameChange} placeholder="Le Grand Huit" />
        </div>

          <button type="submit" class="btn btn-md btn-block" id="addNewElement" > Ajouter </button>
        </form>

    </div>
    );
  }
}
