import React from 'react';
import { Route, Redirect } from 'react-router-dom'
import AjoutMissions from './AjoutMissions/AjoutMissions.js'
import RechercheMissions from './RechercheMissions/RechercheMissions.js'
import Signin from './Signin/Signin.js'
import Signup from './Signup/Signup.js'
import Test from './Test/Test.js'
import MissionPage from './MissionPage/MissionPage.js'
import UserCreated from './UserCreated/UserCreated.js';
import Logout from './Logout/Logout.js'
import PrivateRoute from './PrivateRoute.js'
import * as firebase from 'firebase'

/* const checkAuth = () => {
  alert("rtest")
  firebase.auth().onAuthStateChanged(function (user) {
    if (user) {
      return firebase.database().ref('/users/' + user.uid).once('value').then(function (snapshot) {
        alert("Redirected")
        return true;
        //if(snapshot.val()===null)
        //<Redirect to="/recherchemissions"/>
        
        //var statut = snapshot.val().statut;
        //if(statut==="En attente")document.location.href = '/usercreated'
      });
    } else {
      alert("Not logged in")
      return false;
      
      document.location.href = '/login'
    }
  });
} */

export default class Router extends React.Component {
  state = { loading: true, authenticated: false, user: null };

  componentDidMount() {
    me=this
    firebase.auth().onAuthStateChanged(function (user) {
      if (user) {
        this.setState({
          authenticated: true,
          currentUser: user,
        });
       /*  return firebase.database().ref('/users/' + user.uid).once('value').then(function (snapshot) {
          this.setState({
            authenticated: true,
            currentUser: user,
            loading: false
          });
        }); */
        alert("test")
      } else {
        this.setState({
          authenticated: false,
          currentUser: null,
        });
      }
    });
  }

  render() {
    const { authenticated, loading } = this.state;
    return (
      <div>
        <PrivateRoute path="/ajoutmissions" component={AjoutMissions} authenticated={authenticated}/>
        <Route path="/recherchemissions" component={RechercheMissions} />
        <Route path="/login" component={Signin} />
        <Route path="/signup" component={Signup} />
        <Route path="/test" component={Test} />
        <Route path="/usercreated" component={UserCreated} />
        <Route path="/missionpage/:nomission" component={MissionPage} />
        <Route path="/Logout" component={Logout} />
      </div>
    );
  }
}
