import React, {Component} from 'react';
import './AjoutMissions.css'
import * as firebase from 'firebase';


export default class AjoutMissions extends Component {
  constructor(props) {
        super(props);
        this.state = {
          nomission:1,
          listMissions:[]
        };

        this.getMissions=this.getMissions.bind(this);
        this.componentDidMount=this.componentDidMount.bind(this)
      }

  componentDidMount(){
    const ref = firebase.database().ref('missions');
    //this.setState({nomission:3})
    ref.on('value', snap=>{
      snap.forEach(child=>{
        this.setState({
          nomission:child.val().nomission,
          listMissions:this.state.listMissions.concat(child.val())
      })
      })})

  }
  displayMissions(){
    let listItem=this.state.listMissions.map((mission, index)=>{
      <li key={index}>
        <h2>Numéro de mission:</h2><h3>{mission.nomission}</h3>
        <h2>Date de début:</h2><h3>{mission.datededebut}</h3>
        <h2>Date de fin</h2><h3>{mission.datedefin}</h3>
      </li>
    return(<ul>{listItem}</ul>)
    })


  }
  displayClocks(){
      let listItem = this.state.listClock.map((clock, index) => 
      <li key={index}>
        <Clock date={clock.date} run={clock.run}/>
        <button onClick={() => {this.start(index)}}>Start</button>
        <button onClick={() => {this.stop(index)}}>Stop</button>
        {/* Remarque:​ Une fonction flèche est utilisé dans ce cas pour deux raisons: ne pas avoir à ​bind​ le mot-clef ​this​ à la fonction, et pouvoir utiliser un paramètre chargé dans le contexte actuel (​index​). */}
      </li>
    );
    return(<ul>{listItem}</ul>)
  }


  render() {
    return(
      <div id="wrapper">
      <div class="container">


        <form>
        <br></br>


      <div class="form-group">
      <div class="form-row"> <div class="col-md-6">
          <h3>Numéro de mission:</h3><p>{this.state.nomission}</p>
      </div></div>
      </div>

      {this.displayClocks}

      </form>


      </div>
      </div>

    );

  }
}
