import React,{Component} from 'react';
import './Signin.css'
import * as firebase from 'firebase'

 
export default class Admin extends Component {
  state={listAccounts:"", loading:true}
  
  componentDidMount(){
    this.fetch()
  }
  
  fetch(){
    const ref = firebase.database().ref('users/');
    ref.on('value', snap => {
      snap.forEach(child => {
        alert("child")
        this.setState({
          listAccounts: this.state.listAccounts.concat(child.val()),
          loading:false
        })
      })
    })
  }

  display(){
    this.state.listAccounts.map((account, index)=>{
      return <tr>
        <th>{account.RPPS}</th>
      </tr>
    })
  }

  render() {
    if(loading===true)return <div>Loading...</div>
    return (
      
      <div id="wrapper">
        <form id="login">
        <h2>Accounts Management</h2>

        <div id="container" class="col-md-9">
          <br></br>
          <br></br>
           <div>
            {this.display()}
          </div>
          <br></br>

        </div>
        </form>
      </div>
    );
  }
}
