import React from 'react';
import './AjoutMissions.css'

import listetypedetablissement from '../../Jasons/listetypedetablissement.json'
import listespecialite from '../../Jasons/listespecialite.json'
import listetype from '../../Jasons/listetype.json'

import * as firebase from 'firebase';


export default class AjoutMissions extends React.Component {
  constructor(props) {
        super(props);
        this.state = {
          listMissions:[],
        };

        this.getMissions=this.getMissions.bind(this);
      }

  getMissions(){
    let i=0;
    let nomission1="";
    let statut1= "";

    const ref = firebase.database().ref('missions');
    ref.once('value').then( function(snapshot){
      let mission=snapshot.val();
      nomission1=mission.nomission;
      statut1=mission.statut;
      this.setState({
        listMissions:this.state.listMissions.concat({mission:nomission1, statut:statut1})
      });
      })
      return(
        <div>
          <br></br>
          <br></br>
          <br></br>

        </div>
      )
  }








  render() {


    return(
      <div id="wrapper">

      <div class="form-group">Hello</div>
      <div class="form-row"></div>

      </div>

    );

  }
}
